Values.Value:
{
	/*colors: ['#4572A7', '#AA4643', '#89A54E', '#80699B', '#3D96AE', '#DB843D', '#92A8CD', '#A47D7C', '#B5CA92'],*/
	/*colors: ['#058DC7', '#50B432', '#ED561B', '#DDDF00', '#24CBE5'],*/
	chart: {
		type: 'bubble',
		zoomType: 'xy'
	},
	title: {
		text: null
	},
	credits: {
		enabled: false
	},
	legend: {
		margin: 20,
		borderWidth: 0,
		layout: 'horizontal',
		itemWidth: 150
	},
	xAxis: {
		labels: {
			rotation: -45,
			align: 'right'
		}
	},	
	tooltip: {
		backgroundColor: '#222',
		borderWidth: 0,
		style: {
			color: '#fff'
		},
		useHTML: true,
		formatter: function() {
			var tooltip = '<b>'+ this.point.name +'</b><br/>';
			tooltip +=  this.point.xLegend + ": "+this.point.x+'<br/>';
			tooltip +=  this.point.yLegend + ": "+this.point.y+'<br/>';
			tooltip +=  this.point.sizeLegend + ": "+this.point.z+'<br/>';
			return tooltip;
		},
		valueDecimals: 2
	},
	plotOptions: {
		series: {
			cursor: 'pointer',
			stickyTracking: false,
			shadow: false,
            turboThreshold: 0,
			events: {
				click: function(e) {
                    if (e.point.r) {
                        exa.redirect(buildUrl(this.options.baseUrl, e.point.r));
                    }
				}
			}
		},
         scatter: {
           marker: {
             radius: 5,
               states: {
                 hover: {
                   enabled: true,
                     lineColor: 'rgb(100,100,100)'
                 }
               }
           },
             states: {
               hover: {
                 marker: {
                   enabled: false
                 }
               }
             }
         }
	}
}
